// Receive company IDs from request
const companyIds = $input.first().json.body?.companyIds || $input.first().json.companyIds || [];

// Return items for batch processing
return companyIds.map(id => ({ json: { companyId: id } }));

// Collect all results and extract qualification
const results = $input.all().map(item => {
  const companyId = item.json.companyId || item.json.data?.id?.record_id;
  const values = item.json.data?.values || {};

  // Extract qualification from Attio (yes/no field)
  const qualField = values.qualification || [];
  const qualification = qualField[0]?.option?.title || qualField[0]?.value || null;

  // isProcessed = true if qualification has a value (yes or no)
  const isProcessed = qualification && ['yes', 'no'].includes(qualification.toLowerCase());

  return {
    companyId,
    qualification: qualification ? qualification.toLowerCase() : null,
    isProcessed
  };
});

return [{ json: { statuses: results } }];
